
public class Declaraciones_Operadores_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 5;
		int b = 7;

		b = 7;

		// suma
		int c = a + b;

		System.out.println(c);

		// operador de incremento
		c++;

		System.out.println(c);

		// division
		double d = 10;
		double e = 3;
		double f = d / e;

		System.out.println(f);

	}

}
